package 제어문;

import java.util.*;

public class q1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc=new Scanner(System.in);
		System.out.println("정수를 하나 입력해 주세요");
		int a=sc.nextInt();
		
		if(a%2==0){
			System.out.println("even number");
		}
		else
			System.out.println("odd number");
		

	}

}
